import React, { useState, useEffect } from 'react';
import { BrowserRouter, Route, Switch } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faBars, faCoffee } from '@fortawesome/free-solid-svg-icons'

import './App.css';
import UploadUser from './uploadUser';
import EmployeeDashboard from './employeeDashboard';
import EmployeeCRUD from './employeeCRUD';
import EmployeeAbout from './employeeAbout';

function App() {


  const [count, setCount] = useState(0);

  onMobileMenuChange = event => {
    let fileType = event.target.files[0].type;
    let fileSize = event.target.files[0].size;
    const actualFileSize = Math.round((fileSize / 1024));
    if (fileType === "application/vnd.ms-excel") {
      if (actualFileSize <= 2048) {
        this.setState({ selectedFile: event.target.files[0] });
      }
      else {
        this.setState({ selectedFile: null });
        alert("File too big, please select a file lesser than 2mb");
      }
    }
    else {
      this.setState({ selectedFile: null });
      alert("File format not supported, please select only CSV file");
    }
  };

  return (
    <div className="App">
      <ul className="headerMenuContainer desktop-container">
        <li><a className="active" href="/">HR Management Portal</a></li>
        <li className="floatRight"><a href="/upload">Upload Users</a></li>
        <li className="floatRight"><a href="/dashboard">Employee Dashboard</a></li>
        <li className="floatRight"><a href="/crud">CRUD Feature</a></li>
      </ul>

      <div class="mobile-container">
        <div class="topnav">
          <a href="/" class="active">HR Management Portal</a>
          <div id="myLinks">
            <a href="/upload">Upload Users</a>
            <a href="/dashboard">Employee Dashboard</a>
            <a href="/crud">CRUD Feature</a>
          </div>
          <a href="#" class="icon" onclick={this.onMobileMenuChange}>
             <FontAwesomeIcon icon={faBars} />
          </a>
        </div>
      </div>

      <BrowserRouter>
        <Switch>
          <Route path="/upload">
            <UploadUser />
          </Route>
          <Route path="/dashboard">
            <EmployeeDashboard />
          </Route>
          <Route path="/crud">
            <EmployeeCRUD />
          </Route>
          <Route path="/">
            <EmployeeAbout />
          </Route>
        </Switch>
      </BrowserRouter>

    </div>
  );
}

export default App;
