import React, { Component, useState } from "react";

class UserUpload extends Component() {
    
    constructor(props) {
        super(props);
        this.state = {
            selectedFile: null
        }
    }

    onChangeHandler = event => {
        this.setState({
            selectedFile: event.target.files[0],
            loaded: 0,
        })
    }


    render() {

        return (
            <div className="App">
                <input type="file" name="file" onChange={this.onChangeHandler} />
            </div>
        );

    }

    
}
