import React from 'react';
import { mockEmployee } from "./employeeMockData";
import Table from 'react-responsive-data-table';

class EmployeeDashboard extends React.Component {
  
  state = {
    data: [...mockEmployee],
    columns:[
      {
        name: 'ID',
        selector: 'id',
        sortable: true,
      },
      {
        name: 'User Name',
        selector: 'username',
        sortable: true
      },
      {
        name: 'Full Name',
        selector: 'fullName',
        sortable: true
      },
      {
        name: 'Salary',
        selector: 'salary',
        sortable: true
      },
      {
        name: 'Action',
        selector: 'action',
        sortable: true
      }
    ]
  };

  componentDidMount() {
    
  }

  
  onRowEdit = (row) => {
   console.log(row);
  };
  
  render() {
    return (
      <div className="employeeDashboardConatiner">
        <Table style={{
          opacity: 0.8,
          backgroundColor: "#00113a",
          color: "#ffffff",
          textAlign: "center"
        }}
          tableStyle="table table-hover table-striped table-bordered table-borderless table-responsive"
          pages={true}
          pagination={true}
          onRowClick={row => this.onRowEdit(row)}
          page={true}
          errormsg="Error. . ."
          loadingmsg="Loading. . ."
          isLoading={false}
          sort={true}
          title="Employee"
          search={true}
          size={5}
          data={{
            head: {
              id: "ID",
              username: "User Name",
              fullName: "Full Name",
              salary: "Salary"
            },
            data: [...mockEmployee]
          }} />
      </div>
    );
  }

}


export default EmployeeDashboard;
