import React from 'react';
import { mockEmployee } from "./employeeMockData";
import Table from 'react-responsive-data-table';
import 'react-responsive-modal/styles.css';
import { Modal } from 'react-responsive-modal';

class EmployeeDashboard extends React.Component {

  state = {
    data: [...mockEmployee],
    openModal:false
  };

  componentDidMount() {
    
  }

  
  onRowEdit = (row) => {
    console.log(row);
    this.onOpenModal();
  };

  onOpenModal = () => {
    this.setState({ openModal: true });
  };

  onCloseModal = () => {
    this.setState({ openModal: false });
  };
  
  render() {
    return (
      <div className="employeeDashboardConatiner">
        <Table style={{
          opacity: 0.8,
          backgroundColor: "#00113a",
          color: "#ffffff",
          textAlign: "center"
        }}
          tableStyle="table table-hover table-striped table-bordered table-borderless table-responsive"
          pages={true}
          pagination={true}
          onRowClick={row => this.onRowEdit(row)}
          page={true}
          errormsg="Error. . ."
          loadingmsg="Loading. . ."
          isLoading={false}
          sort={true}
          title="Employee"
          search={true}
          size={5}
          data={{
            head: {
              id: "ID",
              username: "User Name",
              fullName: "Full Name",
              salary: "Salary"
            },
            data: [...mockEmployee]
          }} />

        <div>
          <Modal open={this.state.openModal} onClose={this.onCloseModal} center>
            <div className="modalForm">
              <div>
                ID:
              </div>
              <div>
                Login:
              </div>
              <div>
                Full Name:
              </div>
              <div>
                Salary:
              </div>
            </div>
          </Modal>
        </div>

      </div>
      
    );
  }

}


export default EmployeeDashboard;
