import React from 'react';
import { mockEmployee } from "./employeeMockData";
import Table from 'react-responsive-data-table';

class EmployeeDashboard extends React.Component {
  
  
  render() {
    return (
      
        <Table style={{
          opacity: 0.8,
          backgroundColor: "#00113a",
          color: "#ffffff",
          textAlign: "center"
        }}
          tableStyle="table table-hover table-striped table-bordered table-borderless table-responsive"
          pages={true}
          pagination={true}
          onRowClick={() => { }} // if You Want Table Row Data OnClick then assign this {row => console.log(row)}
          page={true}
          errormsg="Error. . ."
          loadingmsg="Loading. . ."
          isLoading={false}
          sort={true}
          title="Employees"
          search={true}
          size={10}
          data={{
            head: {
              id: "ID",
              username: "User Name",
              fullName: "Full Name",
              salary: "Salary"
            },
            data: [...mockEmployee]
          }} />
      
    );
  }

}


export default EmployeeDashboard;
