import React from 'react';
import { mockEmployee } from "./employeeMockData";
import Table from 'react-responsive-data-table';
import 'react-responsive-modal/styles.css';
import { Modal } from 'react-responsive-modal';

class EmployeeDashboard extends React.Component {

  state = {
    data: [...mockEmployee],
    openModal:false
  };

  componentDidMount() {
    
  }

  
  onRowEdit = (row) => {
    console.log(row);
    this.setState({ currentRow: row });
    this.onOpenModal();
  };

  onOpenModal = () => {
    this.setState({ openModal: true });
  };

  onCloseModal = () => {
    this.setState({ openModal: false });
  };
  
  render() {

    let id = this.state.currentRow ? this.state.currentRow[0] : "";
    let userName = this.state.currentRow ? this.state.currentRow[1] : "";
    let fullName = this.state.currentRow ? this.state.currentRow[2] : "";
    let salary = this.state.currentRow ? this.state.currentRow[3] : "";

    return (
      <div className="employeeDashboardConatiner">
        <Table style={{
          opacity: 0.8,
          backgroundColor: "#00113a",
          color: "#ffffff",
          textAlign: "center"
        }}
          tableStyle="table table-hover table-striped table-bordered table-borderless table-responsive"
          pages={true}
          pagination={true}
          onRowClick={row => this.onRowEdit(row)}
          page={true}
          errormsg="Error. . ."
          loadingmsg="Loading. . ."
          isLoading={false}
          sort={true}
          title="Employee"
          search={true}
          size={5}
          data={{
            head: {
              id: "ID",
              username: "User Name",
              fullName: "Full Name",
              salary: "Salary"
            },
            data: [...mockEmployee]
          }} />

        <div>
          <Modal open={this.state.openModal} onClose={this.onCloseModal} center>
            <div className="modalForm">
              <span className="modalHeader">Edit Employee Details</span>
              <div>
                <span for="fname">ID:</span>
                <input type="text" id="fname" name="fname" disabled="disabled" value={id}></input>
              </div>
              <div>
                <span for="fname">User Name:</span>
                <input type="text" id="fname" name="fname" disabled="disabled" value={userName}></input>
              </div>
              <div>
                <span for="fname">Full Name:</span>
                <input type="text" id="fname" name="fname" value={fullName}></input>
              </div>
              <div>
                <span for="fname">Salary:</span>
                <input type="text" id="fname" name="fname" value={salary}></input>
              </div>
              <div>
                <input type="button" id="btnSave" className="btnSave" value="Update"></input>
                <input type="button" id="btnCancel" className="btnCancel" value="Cancel" onClick={this.onCloseModal}></input>
              </div>
            </div>
          </Modal>
        </div>

      </div>
      
    );
  }

}


export default EmployeeDashboard;
