import React from 'react';
import { mockEmployee } from "./employeeMockData";
import DataTable, { createTheme } from 'react-data-table-component';

class EmployeeDashboard extends React.Component {
  
  state = {
    data: [...mockEmployee],
    columns:[
      {
        name: 'ID',
        selector: 'id',
        sortable: true,
      },
      {
        name: 'User Name',
        selector: 'username',
        sortable: true
      },
      {
        name: 'Full Name',
        selector: 'fullName',
        sortable: true
      },
      {
        name: 'Salary',
        selector: 'salary',
        sortable: true
      }
    ]
  };

  componentDidMount() {

    createTheme('solarized', {
      text: {
        primary: '#268bd2',
        secondary: '#2aa198',
      },
      background: {
        default: '#002b36',
      },
      context: {
        background: '#cb4b16',
        text: '#FFFFFF',
      },
      divider: {
        default: '#073642',
      },
      action: {
        button: 'rgba(0,0,0,.54)',
        hover: 'rgba(0,0,0,.08)',
        disabled: 'rgba(0,0,0,.12)',
      },
    });
    
  }

  

  
  render() {
    return (
      <div className="employeeDashboardConatiner">
        <DataTable
          title="Employees"
          columns={this.state.columns}
          data={this.state.data}
          pagination={true}
          paginationRowsPerPageOptions={[10, 15, 20, 25, 30]}
          theme="dark"
        />
      </div>
    );
  }

}


export default EmployeeDashboard;
